import React from 'react';
import ItemUser from './ItemUser';

const ViewUser = ({user}) => {  

  const displayusers = user?.map(users => {
    // var userimage = ""
    // users.images.map(image => {
    //   // console.log(image.image1)
    //   userimage = image.image1
    //   return userimage
    // })

    // console.log(userimage)
    // console.log(users.name)

    return <ItemUser key = {users.id} id = {users.id} name = {users.name} email ={users.email} />
  })

  return (
     <div>
        <div className="page overflow-x-auto shadow-md sm:rounded-lg users">
    <table className="w-full text-sm text-left text-gray-500 dark:text-gray-400">
        <thead className="text-xs text-gray-700 uppercase bg-gray-50 dark:bg-gray-700 dark:text-gray-400">
        <tr>
        <th scope="col" className="px-6 py-3">
            ID
            </th>
            <th scope="col" className="px-6 py-3">
            user name
            </th>
            <th scope="col" className="px-6 py-3">
            Email
            </th>
            <th scope="col" className="px-6 py-3">
            Edit
            </th>
            <th scope="col" className="px-6 py-3">
            Delete
            </th>
        </tr>
        </thead>
        <tbody>

            {displayusers}
        </tbody>
    </table>
    </div>

     </div> 


  )
}

export default ViewUser;
